import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { PersonModalComponent } from '../person-form/person-modal/person-modal.component';
import { PresencaModalComponent } from '../presenca/presenca-modal/presenca-modal.component';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {
  constructor(private dialog: MatDialog) {}

  abrirModalCadastro() {
    const dialogRef = this.dialog.open(PersonModalComponent, {
      width: '500px',
      disableClose: true
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        window.location.reload();
      }
    });
  }

  abrirModalPresenca() {
    const dialogRef = this.dialog.open(PresencaModalComponent, {
      width: '500px',
      disableClose: true
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        window.location.reload();
      }
    });
  }
}
