import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { MatSnackBar } from '@angular/material/snack-bar';

@Component({
  selector: 'app-person-form',
  templateUrl: './person-form.component.html',
  styleUrls: ['./person-form.component.css']
})
export class PersonFormComponent implements OnInit {
  personForm: FormGroup;
  people: any[] = [];
  editingId: number | null = null;

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private snackBar: MatSnackBar
  ) {
    this.personForm = this.fb.group({
      name: ['', Validators.required],
      birth_date: ['', Validators.required],
      address: ['', Validators.required]
    });
  }

  ngOnInit() {
    this.loadPeople();
  }

  loadPeople() {
    this.http.get<any[]>('http://localhost:8000/people/').subscribe(
      data => this.people = data,
      error => {
        console.error('Erro ao carregar pessoas:', error);
        this.showMessage('Erro ao carregar pessoas: ' + this.getErrorMessage(error));
      }
    );
  }

  onSubmit() {
    if (this.personForm.valid) {
      const formData = this.personForm.value;
      console.log('Dados do formulário:', formData); // Debug

      const personData = {
        name: formData.name,
        birth_date: formData.birth_date.toISOString().split('T')[0],
        address: formData.address
      };
      
      console.log('Dados a serem enviados:', personData); // Debug

      if (this.editingId) {
        this.http.put(`http://localhost:8000/people/${this.editingId}`, personData).subscribe(
          () => {
            this.loadPeople();
            this.resetForm();
            this.showMessage('Pessoa atualizada com sucesso!');
          },
          error => {
            console.error('Erro ao atualizar pessoa:', error);
            this.showMessage('Erro ao atualizar pessoa: ' + this.getErrorMessage(error));
          }
        );
      } else {
        this.http.post('http://localhost:8000/people/', personData).subscribe(
          () => {
            this.loadPeople();
            this.resetForm();
            this.showMessage('Pessoa cadastrada com sucesso!');
          },
          error => {
            console.error('Erro ao criar pessoa:', error);
            this.showMessage('Erro ao cadastrar pessoa: ' + this.getErrorMessage(error));
          }
        );
      }
    }
  }

  editPerson(person: any) {
    this.editingId = person.id;
    this.personForm.patchValue({
      name: person.name,
      birth_date: new Date(person.birth_date),
      address: person.address
    });
  }

  deletePerson(id: number) {
    if (confirm('Tem certeza que deseja excluir esta pessoa?')) {
      this.http.delete(`http://localhost:8000/people/${id}`).subscribe(
        () => {
          this.loadPeople();
          this.showMessage('Pessoa excluída com sucesso!');
        },
        error => {
          console.error('Erro ao excluir pessoa:', error);
          this.showMessage('Erro ao excluir pessoa: ' + this.getErrorMessage(error));
        }
      );
    }
  }

  resetForm() {
    this.personForm.reset();
    this.editingId = null;
  }

  private showMessage(message: string) {
    this.snackBar.open(message, 'Fechar', {
      duration: 5000,
      horizontalPosition: 'center',
      verticalPosition: 'top'
    });
  }

  private getErrorMessage(error: HttpErrorResponse): string {
    if (error.error instanceof ErrorEvent) {
      return error.error.message;
    } else {
      return error.error?.detail || error.message || 'Erro desconhecido';
    }
  }
}
