import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { PersonService } from '../../services/person.service';
import { Person } from '../../models/person.model';
import { DateAdapter, MAT_DATE_FORMATS, MAT_DATE_LOCALE } from '@angular/material/core';
import { MomentDateAdapter, MAT_MOMENT_DATE_ADAPTER_OPTIONS } from '@angular/material-moment-adapter';
import * as moment from 'moment';

export const MY_FORMATS = {
  parse: {
    dateInput: 'DD/MM/YYYY',
  },
  display: {
    dateInput: 'DD/MM/YYYY',
    monthYearLabel: 'MMM YYYY',
    dateA11yLabel: 'LL',
    monthYearA11yLabel: 'MMMM YYYY',
  },
};

@Component({
  selector: 'app-person-modal',
  templateUrl: './person-modal.component.html',
  styleUrls: ['./person-modal.component.css'],
  providers: [
    {
      provide: DateAdapter,
      useClass: MomentDateAdapter,
      deps: [MAT_DATE_LOCALE, MAT_MOMENT_DATE_ADAPTER_OPTIONS]
    },
    { provide: MAT_DATE_FORMATS, useValue: MY_FORMATS }
  ]
})
export class PersonModalComponent implements OnInit {
  personForm: FormGroup;
  isEditMode: boolean = false;
  selectedFile: File | null = null;
  previewUrl: string | null = null;
  isSubmitting = false;

  constructor(
    private fb: FormBuilder,
    private personService: PersonService,
    public dialogRef: MatDialogRef<PersonModalComponent>,
    @Inject(MAT_DIALOG_DATA) public data: { person: Person | null }
  ) {
    this.personForm = this.fb.group({
      name: ['', Validators.required],
      birth_date: ['', Validators.required],
      alergia: [''],
      foto: [''],
      responsavel: ['', Validators.required],
      grau_parentesco: ['', Validators.required],
      sexo: ['', Validators.required],
      endereco: ['', Validators.required],
      bairro: ['', Validators.required],
      cidade: ['', Validators.required],
      estado: ['', Validators.required],
      cep: ['', Validators.required],
      telefone: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      observacoes: ['']
    });
  }

  filterDate(date: Date | null): boolean {
    if (!date) return false;
    const today = new Date();
    return date <= today;
  }

  ngOnInit(): void {
    if (this.data?.person) {
      this.isEditMode = true;
      const person = this.data.person;
      this.personForm.patchValue({
        name: person.name,
        birth_date: person.birth_date,
        alergia: person.alergia,
        foto: person.foto,
        responsavel: person.responsavel,
        grau_parentesco: person.grau_parentesco,
        sexo: person.sexo,
        endereco: person.endereco,
        bairro: person.bairro,
        cidade: person.cidade,
        estado: person.estado,
        cep: person.cep,
        telefone: person.telefone,
        email: person.email,
        observacoes: person.observacoes
      });
      if (person.foto) {
        this.previewUrl = person.foto;
      }
    }
  }

  onFileSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files[0]) {
      this.selectedFile = input.files[0];
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.previewUrl = e.target.result;
      };
      reader.readAsDataURL(this.selectedFile);
    }
  }

  onSubmit(): void {
    if (this.personForm.valid) {
      this.isSubmitting = true;
      const formValue = this.personForm.value;

      // Prepare the payload
      const payload = {
        name: formValue.name,
        birth_date: formValue.birth_date,
        alergia: formValue.alergia,
        foto: this.previewUrl,
        responsavel: formValue.responsavel,
        grau_parentesco: formValue.grau_parentesco,
        sexo: formValue.sexo,
        endereco: formValue.endereco,
        bairro: formValue.bairro,
        cidade: formValue.cidade,
        estado: formValue.estado,
        cep: formValue.cep,
        telefone: formValue.telefone,
        email: formValue.email,
        observacoes: formValue.observacoes
      };

      if (this.isEditMode && this.data.person) {
        this.personService.updatePerson(this.data.person.id, payload).subscribe({
          next: () => {
            this.dialogRef.close(true);
          },
          error: (error) => {
            console.error('Error updating person:', error);
            this.isSubmitting = false;
          }
        });
      } else {
        this.personService.createPerson(payload).subscribe({
          next: () => {
            this.dialogRef.close(true);
          },
          error: (error) => {
            console.error('Error creating person:', error);
            this.isSubmitting = false;
          }
        });
      }
    }
  }

  onCancel(): void {
    this.dialogRef.close();
  }
}
