import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';

@Component({
  selector: 'app-person-modal',
  templateUrl: './person-modal.component.html',
  styleUrls: ['./person-modal.component.css']
})
export class PersonModalComponent implements OnInit {
  personForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private dialogRef: MatDialogRef<PersonModalComponent>,
    private snackBar: MatSnackBar
  ) {
    this.personForm = this.fb.group({
      name: ['', Validators.required],
      birth_date: [new Date(), Validators.required],
      address: ['']
    });
  }

  ngOnInit() {}

  onSubmit() {
    if (this.personForm.valid) {
      const personData = this.personForm.value;
      console.log('Dados da pessoa:', personData);

      // Formatar a data para o formato esperado pelo backend
      const data = new Date(personData.birth_date);
      const formattedData = {
        name: personData.name,
        birth_date: data.toISOString().split('T')[0], // Formato YYYY-MM-DD
        address: personData.address || null
      };

      console.log('Dados formatados:', formattedData);

      this.http.post('http://localhost:8000/people/', formattedData).subscribe(
        response => {
          console.log('Pessoa cadastrada com sucesso:', response);
          this.showMessage('Pessoa cadastrada com sucesso!');
          this.dialogRef.close(true);
        },
        (error: HttpErrorResponse) => {
          console.error('Erro ao cadastrar pessoa:', error);
          let errorMessage = 'Erro ao cadastrar pessoa';
          if (error.error && error.error.detail) {
            if (Array.isArray(error.error.detail)) {
              errorMessage += ': ' + error.error.detail.map((err: any) => err.msg).join(', ');
            } else {
              errorMessage += ': ' + error.error.detail;
            }
          }
          this.showMessage(errorMessage, true);
        }
      );
    }
  }

  onCancel() {
    this.dialogRef.close();
  }

  private showMessage(message: string, isError: boolean = false) {
    this.snackBar.open(message, 'Fechar', {
      duration: 5000,
      panelClass: isError ? ['error-snackbar'] : ['success-snackbar']
    });
  }
}
