import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { MatSnackBar } from '@angular/material/snack-bar';

@Component({
  selector: 'app-presenca',
  templateUrl: './presenca.component.html',
  styleUrls: ['./presenca.component.css']
})
export class PresencaComponent implements OnInit {
  presencaForm: FormGroup;
  pessoas: any[] = [];
  presencas: any[] = [];

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private snackBar: MatSnackBar
  ) {
    this.presencaForm = this.fb.group({
      pessoa_id: ['', [Validators.required, Validators.min(1)]],
      date: [new Date(), Validators.required],
      observation: ['']
    });
  }

  ngOnInit() {
    this.carregarPessoas();
    this.carregarPresencas();
  }

  carregarPessoas() {
    this.http.get<any[]>('http://localhost:8000/people/').subscribe(
      data => this.pessoas = data,
      error => {
        console.error('Erro ao carregar pessoas:', error);
        this.showMessage('Erro ao carregar pessoas', true);
      }
    );
  }

  carregarPresencas() {
    this.http.get<any[]>('http://localhost:8000/presences/').subscribe(
      data => this.presencas = data,
      error => {
        console.error('Erro ao carregar presenças:', error);
        this.showMessage('Erro ao carregar presenças', true);
      }
    );
  }

  onSubmit() {
    if (this.presencaForm.valid) {
      const presencaData = this.presencaForm.value;
      console.log('Dados da presença:', presencaData);

      // Formatar a data para o formato esperado pelo backend
      const data = new Date(presencaData.date);
      const formattedData = {
        person_id: parseInt(presencaData.pessoa_id),
        date: data.toISOString().split('T')[0], // Formato YYYY-MM-DD
        observation: presencaData.observation || null
      };

      console.log('Dados formatados:', formattedData);

      this.http.post('http://localhost:8000/presences/', formattedData).subscribe(
        response => {
          console.log('Presença registrada com sucesso:', response);
          this.showMessage('Presença registrada com sucesso!');
          this.presencaForm.reset({
            date: new Date(),
            observation: ''
          });
          this.carregarPresencas();
        },
        (error: HttpErrorResponse) => {
          console.error('Erro ao registrar presença:', error);
          let errorMessage = 'Erro ao registrar presença';
          if (error.error && error.error.detail) {
            if (Array.isArray(error.error.detail)) {
              errorMessage += ': ' + error.error.detail.map((err: any) => err.msg).join(', ');
            } else {
              errorMessage += ': ' + error.error.detail;
            }
          }
          this.showMessage(errorMessage, true);
        }
      );
    }
  }

  private showMessage(message: string, isError: boolean = false) {
    this.snackBar.open(message, 'Fechar', {
      duration: 5000,
      panelClass: isError ? ['error-snackbar'] : ['success-snackbar']
    });
  }

  getPessoaName(pessoaId: number): string {
    const pessoa = this.pessoas.find(p => p.id === pessoaId);
    return pessoa ? pessoa.name : 'Pessoa não encontrada';
  }
}
