import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <div class="app-container">
      <header>
        <h1>Sistema de Cadastro de Pessoas</h1>
      </header>
      <main>
        <app-person-form></app-person-form>
      </main>
    </div>
  `,
  styles: [`
    .app-container {
      min-height: 100vh;
      background-color: #f5f5f5;
    }
    
    header {
      background-color: #1976d2;
      color: white;
      padding: 1rem;
      text-align: center;
    }
    
    h1 {
      margin: 0;
      font-size: 1.5rem;
    }
    
    main {
      padding: 2rem 0;
    }
  `]
})
export class AppComponent {
  title = 'cadastro-pessoas';
}
